            <DialogFooter className="mt-8 pt-6 border-t border-border gap-3">
              <Button
                variant="outline"
                onClick={() => setShowEditForm(false)}
                className="px-6 py-3 border-2 border-border hover:bg-secondary transition-all duration-200"
              >
                Cancel
