    const errors: Partial<RosterFormData> = {}

    if (!data.team_id) {
      errors.team_id = 'Team selection is required'
    }
    if (!data.start_date) {
      errors.start_date = 'Start date is required'
    }

    setRosterFormErrors(errors)
    return Object.keys(errors).length === 0
  }

