          </DialogContent>
        </Dialog>

        {/* Assign to Team Modal */}
        <Dialog open={showRosterModal} onOpenChange={setShowRosterModal}>
          <DialogContent className="glass-card border-gray-200/50 dark:border-gray-700/50">
