app.post('/api/registrations', authenticateUser, async (req, res) => {
    const { user_id, team_id, player_id, registration_fee, notes } = req.body;

    // Validate required fields
    if (!user_id || !team_id || !player_id || !registration_fee) {
        return res.status(400).json({
            error: 'user_id, team_id, player_id, and registration_fee are required'
        });
    }

    // Validate registration fee is positive
    if (registration_fee <= 0) {
        return res.status(400).json({ error: 'Registration fee must be greater than 0' });
    }

    try {
        // Check if registration already exists for this combination
        const { data: existingRegistration, error: checkError } = await supabase
            .from('registrations')
            .select('id')
            .eq('user_id', user_id)
            .eq('team_id', team_id)
            .eq('player_id', player_id)
            .single();

        if (existingRegistration) {
            return res.status(409).json({
                error: 'Registration already exists for this user/team/player combination'
            });
        }

        // Create the registration
        const { data: registration, error } = await supabase
            .from('registrations')
            .insert([{
                user_id,
                team_id,
                player_id,
                registration_fee,
                notes: notes || null
            }])
            .select()
            .single();

        if (error) {
            console.error('Registration creation error:', error);
            return res.status(500).json({ error: 'Failed to create registration' });
        }

        res.status(201).json({
            message: 'Registration created successfully',
            registration
        });
    } catch (error) {
        console.error('Create registration error:', error);
        res.status(500).json({ error: 'Internal server error' });
    }
});

// GET /api/registrations - List registrations with filtering
app.get('/api/registrations', authenticateUser, async (req, res) => {
    const {
        user_id,
        team_id,
        player_id,
        status,
        page = 1,
        limit = 10
    } = req.query;

    const offset = (parseInt(page) - 1) * parseInt(limit);

    try {
        let query = supabase
            .from('registrations')
            .select(`
                *,
                users (
                    id,
                    first_name,
                    last_name,
                    email,
                    organization
                ),
                teams (
                    id,
                    name,
                    organization,
                    division,
                    age_group,
                    skill_level
                ),
                players (
                    id,
                    first_name,
                    last_name,
                    email,
                    phone,
                    date_of_birth
                )
            `, { count: 'exact' });

        // Apply filters
        if (user_id) {
            query = query.eq('user_id', user_id);
        }
        if (team_id) {
            query = query.eq('team_id', team_id);
        }
        if (player_id) {
            query = query.eq('player_id', player_id);
        }
        if (status) {
            query = query.eq('status', status);
        }

        // Add pagination and ordering
        query = query
            .order('registration_date', { ascending: false })
            .range(offset, offset + parseInt(limit) - 1);

        const { data, error, count } = await query;

        if (error) {
            console.error('Registrations fetch error:', error);
            return res.status(500).json({ error: 'Failed to fetch registrations' });
        }

        res.json({
            registrations: data || [],
            pagination: {
                page: parseInt(page),
                limit: parseInt(limit),
                total: count || 0,
                totalPages: Math.ceil((count || 0) / parseInt(limit))
            }
        });
    } catch (error) {
        console.error('Get registrations error:', error);
        res.status(500).json({ error: 'Internal server error' });
    }
});

