            const { data: existingJersey, error: jerseyCheckError } = await supabase
                .from('roster_entries')
                .select('id')
                .eq('team_id', currentEntry.team_id)
                .eq('jersey_number', jersey_number)
                .is('end_date', null)
                .neq('id', roster_entry_id);

