app.get('/api/reports/player-contact', authenticateUser, async (req, res) => {
    try {
        const { team_id, format = 'json' } = req.query;

        // Validate format parameter
        const validFormats = ['json', 'csv'];
        if (!validFormats.includes(format)) {
