@tailwind base;
@tailwind components;
@tailwind utilities;

@layer base {
  :root {
    --background: 0 0% 100%;
    --foreground: 0 0% 10%;
    --card: 0 0% 100%;
    --card-foreground: 0 0% 10%;
    --popover: 0 0% 100%;
    --popover-foreground: 0 0% 10%;
    --primary: 0 0% 9%;
    --primary-foreground: 0 0% 98%;
    --secondary: 0 0% 96%;
    --secondary-foreground: 0 0% 9%;
    --muted: 0 0% 96%;
    --muted-foreground: 0 0% 45%;
    --accent: 0 0% 96%;
    --accent-foreground: 0 0% 9%;
    --destructive: 0 0% 20%;
    --destructive-foreground: 0 0% 98%;
    --border: 0 0% 90%;
    --input: 0 0% 90%;
    --ring: 0 0% 64%;
    --radius: 1rem;
  }

  .dark {
    --background: 0 0% 5%;
    --foreground: 0 0% 98%;
    --card: 0 0% 8%;
    --card-foreground: 0 0% 98%;
    --popover: 0 0% 8%;
    --popover-foreground: 0 0% 98%;
    --primary: 0 0% 98%;
    --primary-foreground: 0 0% 9%;
    --secondary: 0 0% 14%;
    --secondary-foreground: 0 0% 98%;
    --muted: 0 0% 14%;
    --muted-foreground: 0 0% 64%;
    --accent: 0 0% 14%;
    --accent-foreground: 0 0% 98%;
    --destructive: 0 0% 50%;
    --destructive-foreground: 0 0% 98%;
    --border: 0 0% 14%;
    --input: 0 0% 14%;
    --ring: 0 0% 84%;
  }

  body {
    @apply bg-background text-foreground antialiased;
    font-family: -apple-system, BlinkMacSystemFont, "Segoe UI", Roboto, Helvetica, Arial, sans-serif;
    background: linear-gradient(to bottom right, #f8f8f8, #e5e5e5);
    min-height: 100vh;
    position: relative;
  }

  body.dark {
    background: linear-gradient(to bottom right, #0a0a0a, #1a1a1a);
  }

  * {
    @apply border-border;
  }
}

@layer components {
  .glass-card {
    @apply bg-white/80 dark:bg-gray-900/80 backdrop-blur-xl border border-gray-200/50 dark:border-gray-700/50 shadow-xl rounded-2xl;
  }

  .glass-card-hover {
    @apply hover:bg-white/90 dark:hover:bg-gray-900/90 hover:shadow-2xl transition-all duration-500;
  }

  .gradient-text {
    @apply bg-gradient-to-r from-gray-900 via-gray-600 to-gray-900 dark:from-gray-100 dark:via-gray-400 dark:to-gray-100 bg-clip-text text-transparent;
  }

  .button-primary {
    @apply relative overflow-hidden bg-gradient-to-r from-gray-800 to-black dark:from-white dark:to-gray-200 text-white dark:text-black font-semibold py-3 px-6 rounded-xl shadow-lg hover:shadow-2xl transform transition-all duration-300 hover:scale-105;
  }

  .button-primary::before {
    content: '';
    @apply absolute inset-0 bg-gradient-to-r from-black to-gray-700 dark:from-gray-200 dark:to-white opacity-0 transition-opacity duration-300;
  }

  .button-primary:hover::before {
    @apply opacity-100;
  }

  .button-primary span {
    @apply relative z-10;
  }

  .card-shadow {
    box-shadow:
      0 10px 40px rgba(0, 0, 0, 0.1),
      0 2px 10px rgba(0, 0, 0, 0.05),
      inset 0 1px 0 rgba(255, 255, 255, 0.1);
  }

  .dark .card-shadow {
    box-shadow:
      0 10px 40px rgba(0, 0, 0, 0.4),
      0 2px 10px rgba(0, 0, 0, 0.2),
      inset 0 1px 0 rgba(255, 255, 255, 0.05);
  }

  .glow-border {
    box-shadow:
      0 0 20px rgba(0, 0, 0, 0.1),
      0 0 40px rgba(0, 0, 0, 0.05),
      inset 0 0 20px rgba(0, 0, 0, 0.03);
  }

  .dark .glow-border {
    box-shadow:
      0 0 20px rgba(255, 255, 255, 0.1),
      0 0 40px rgba(255, 255, 255, 0.05),
      inset 0 0 20px rgba(255, 255, 255, 0.03);
  }

  .shimmer-effect {
    background: linear-gradient(
      90deg,
      transparent 0%,
      rgba(255, 255, 255, 0.2) 50%,
      transparent 100%
    );
    background-size: 200% 100%;
    animation: shimmer 2s linear infinite;
  }

  .floating-element {
    animation: float 6s ease-in-out infinite;
  }

  .pulse-glow {
    animation: pulseGlow 2s cubic-bezier(0.4, 0, 0.6, 1) infinite;
  }

  input:focus,
  textarea:focus,
  select:focus {
    @apply ring-2 ring-gray-400 ring-opacity-50 border-gray-400 outline-none transition-all duration-200;
  }

  .scrollbar-custom {
    scrollbar-width: thin;
    scrollbar-color: rgba(0, 0, 0, 0.2) transparent;
  }

  .dark .scrollbar-custom {
    scrollbar-color: rgba(255, 255, 255, 0.2) transparent;
  }

  .scrollbar-custom::-webkit-scrollbar {
    width: 8px;
    height: 8px;
  }

  .scrollbar-custom::-webkit-scrollbar-track {
    background: transparent;
  }

  .scrollbar-custom::-webkit-scrollbar-thumb {
    background: linear-gradient(180deg, #888, #555);
    border-radius: 4px;
  }

  .dark .scrollbar-custom::-webkit-scrollbar-thumb {
    background: linear-gradient(180deg, #555, #888);
  }

  .scrollbar-custom::-webkit-scrollbar-thumb:hover {
    background: linear-gradient(180deg, #666, #333);
  }

  .dark .scrollbar-custom::-webkit-scrollbar-thumb:hover {
    background: linear-gradient(180deg, #777, #aaa);
  }
}

@layer utilities {
  @keyframes shimmer {
    0% {
      background-position: -200% 0;
    }
    100% {
      background-position: 200% 0;
    }
  }

  @keyframes float {
    0%, 100% {
      transform: translateY(0);
    }
    50% {
      transform: translateY(-20px);
    }
  }

  @keyframes pulseGlow {
    0%, 100% {
      opacity: 1;
    }
    50% {
      opacity: 0.5;
    }
  }

  @keyframes fadeIn {
    0% {
      opacity: 0;
    }
    100% {
      opacity: 1;
    }
  }

  @keyframes slideUp {
    0% {
      transform: translateY(20px);
      opacity: 0;
    }
    100% {
      transform: translateY(0);
      opacity: 1;
    }
  }

  @keyframes slideDown {
    0% {
      transform: translateY(-20px);
      opacity: 0;
    }
    100% {
      transform: translateY(0);
      opacity: 1;
    }
  }

  @keyframes scale {
    0% {
      transform: scale(0.95);
      opacity: 0;
    }
    100% {
      transform: scale(1);
      opacity: 1;
    }
  }

  .animate-fade-in {
    animation: fadeIn 0.5s ease-in-out;
  }

  .animate-slide-up {
    animation: slideUp 0.5s ease-out;
  }

  .animate-slide-down {
    animation: slideDown 0.5s ease-out;
  }

  .animate-scale {
    animation: scale 0.3s ease-in-out;
  }

  .animation-delay-200 {
    animation-delay: 200ms;
  }

  .animation-delay-400 {
    animation-delay: 400ms;
  }

  .animation-delay-600 {
    animation-delay: 600ms;
  }
}

