CREATE INDEX idx_registrations_team_id ON registrations(team_id);
CREATE INDEX idx_registrations_player_id ON registrations(player_id);
CREATE INDEX idx_registrations_status ON registrations(status);

CREATE INDEX idx_payments_registration_id ON payments(registration_id);
CREATE INDEX idx_payments_status ON payments(status);
CREATE INDEX idx_payments_payment_method ON payments(payment_method);

-- Add update triggers
CREATE OR REPLACE FUNCTION update_updated_at_column()
RETURNS TRIGGER AS $$
BEGIN
    NEW.updated_at = now();
    RETURN NEW;
END;
$$ language 'plpgsql';

CREATE TRIGGER update_registrations_updated_at BEFORE UPDATE ON registrations FOR EACH ROW EXECUTE FUNCTION update_updated_at_column();
CREATE TRIGGER update_payments_updated_at BEFORE UPDATE ON payments FOR EACH ROW EXECUTE FUNCTION update_updated_at_column();

-- Create registrations table for tracking player/team registrations
CREATE TABLE registrations (
    id UUID PRIMARY KEY DEFAULT gen_random_uuid(),
    player_id INTEGER NOT NULL REFERENCES players(id) ON DELETE CASCADE,
    team_id INTEGER NOT NULL REFERENCES teams(id) ON DELETE CASCADE,
    registration_date DATE NOT NULL DEFAULT CURRENT_DATE,
    amount_due DECIMAL(10,2) NOT NULL CHECK (amount_due >= 0),
    amount_paid DECIMAL(10,2) NOT NULL DEFAULT 0 CHECK (amount_paid >= 0),
    balance_due DECIMAL(10,2) GENERATED ALWAYS AS (amount_due - amount_paid) STORED,
    status VARCHAR(20) NOT NULL DEFAULT 'Pending' CHECK (status IN ('Pending', 'Partial', 'Complete', 'Cancelled')),
    created_at TIMESTAMPTZ NOT NULL DEFAULT NOW(),
    updated_at TIMESTAMPTZ NOT NULL DEFAULT NOW()
);

-- Create payments table for tracking payment transactions
CREATE TABLE payments (
    id UUID PRIMARY KEY DEFAULT gen_random_uuid(),
    registration_id UUID NOT NULL REFERENCES registrations(id) ON DELETE CASCADE,
    amount DECIMAL(10,2) NOT NULL CHECK (amount > 0),
    payment_method VARCHAR(50) NOT NULL CHECK (payment_method IN ('credit_card', 'debit_card', 'bank_transfer', 'cash', 'check', 'online_payment')),
    payment_method_details JSONB,
    status VARCHAR(20) NOT NULL DEFAULT 'Pending' CHECK (status IN ('Pending', 'Completed', 'Failed', 'Refunded')),
    transaction_id VARCHAR(255),
    processed_at TIMESTAMPTZ,
    created_at TIMESTAMPTZ NOT NULL DEFAULT NOW(),
    updated_at TIMESTAMPTZ NOT NULL DEFAULT NOW()
);

-- Create indexes for better query performance
CREATE INDEX idx_registrations_player_id ON registrations(player_id);
CREATE INDEX idx_registrations_team_id ON registrations(team_id);
CREATE INDEX idx_registrations_status ON registrations(status);
CREATE INDEX idx_registrations_registration_date ON registrations(registration_date);
CREATE INDEX idx_payments_registration_id ON payments(registration_id);
CREATE INDEX idx_payments_status ON payments(status);
CREATE INDEX idx_payments_transaction_id ON payments(transaction_id);
CREATE INDEX idx_payments_processed_at ON payments(processed_at);

-- Create function to update updated_at timestamp
CREATE OR REPLACE FUNCTION update_updated_at_column()
RETURNS TRIGGER AS $$
BEGIN
    NEW.updated_at = NOW();
    RETURN NEW;
END;
$$ LANGUAGE plpgsql;

-- Create triggers to automatically update updated_at timestamps
CREATE TRIGGER update_registrations_updated_at
    BEFORE UPDATE ON registrations
    FOR EACH ROW
    EXECUTE FUNCTION update_updated_at_column();

CREATE TRIGGER update_payments_updated_at
    BEFORE UPDATE ON payments
    FOR EACH ROW
    EXECUTE FUNCTION update_updated_at_column();

-- Create trigger to automatically update registration status based on payments
CREATE OR REPLACE FUNCTION update_registration_status()
RETURNS TRIGGER AS $$
BEGIN
    -- Update the registration status based on the balance_due
    UPDATE registrations 
    SET status = CASE 
        WHEN balance_due <= 0 THEN 'Complete'
        WHEN amount_paid > 0 AND balance_due > 0 THEN 'Partial'
        WHEN amount_paid = 0 THEN 'Pending'
        ELSE status
    END
    WHERE id = COALESCE(NEW.registration_id, OLD.registration_id);

    RETURN COALESCE(NEW, OLD);
END;
$$ LANGUAGE plpgsql;

-- Create trigger to update registration status when payments change
CREATE TRIGGER update_registration_status_on_payment_change
    AFTER INSERT OR UPDATE OR DELETE ON payments
    FOR EACH ROW
    EXECUTE FUNCTION update_registration_status();

-- Create trigger to update amount_paid in registrations when payments change
CREATE OR REPLACE FUNCTION update_registration_amount_paid()
RETURNS TRIGGER AS $$
BEGIN
    -- Recalculate amount_paid for the registration
    UPDATE registrations 
    SET amount_paid = COALESCE((
        SELECT SUM(amount) 
        FROM payments 
        WHERE registration_id = COALESCE(NEW.registration_id, OLD.registration_id)
        AND status = 'Completed'
    ), 0)
    WHERE id = COALESCE(NEW.registration_id, OLD.registration_id);

    RETURN COALESCE(NEW, OLD);
END;
$$ LANGUAGE plpgsql;

-- Create trigger to update amount_paid when payments change
CREATE TRIGGER update_registration_amount_paid_on_payment_change
    AFTER INSERT OR UPDATE OR DELETE ON payments
    FOR EACH ROW
    EXECUTE FUNCTION update_registration_amount_paid();

-- Add comments to document the tables
COMMENT ON TABLE registrations IS 'Tracks player/team registrations with payment information';
COMMENT ON TABLE payments IS 'Tracks individual payment transactions for registrations';
COMMENT ON COLUMN registrations.balance_due IS 'Computed field: amount_due - amount_paid';
COMMENT ON COLUMN payments.payment_method_details IS 'JSON field for storing payment gateway information, card details, etc.';
COMMENT ON COLUMN payments.transaction_id IS 'External payment gateway transaction reference';

